import React from 'react';
import TestData from './test_data.json';
import './App.css';

import NewsCard from '../NewsCard/NewsCard';
import Graph from '../Graph/Graph';
import Grid from '@material-ui/core/Grid';


class App extends React.Component{
    constructor(){
        super();
        this.state = {
            data:{}, 
            focused_news:{},
            loaded: false
        }
    }

    componentDidMount(){
        this.loadNews();
    }
    loadNews(){
        this.setState({
            data:TestData,
            focused_news: TestData.focused_news,
            loaded:true
        });
    }

    generateGraphData(graphData){
        const nodes = graphData.news.map(news => ({id: news.digest, news: news}));
        console.log("nodes:" + nodes);
        console.log("Links:" + graphData.graph.links);
        const gData = {
            nodes: nodes,
            links: graphData.graph.links.map(link => ({
                source:link.from,
                target:link.to
            }))
        };
        return gData;
    }


    handleNodeClick(node){
        this.setState({
            focused_news: node.news
        })

    }
    render() {
        if (this.state.loaded === false){
            return (<div>Loading...</div>)
        }

        const graphData = this.generateGraphData(this.state.data);

        return(<div>
                 <Grid container>
                    <Grid item md={8}>
                      <Graph graphData = {graphData} handleNodeClick = {(node) => this.handleNodeClick(node)} />
                    </Grid>
                    <Grid item md={4}>
                      <NewsCard news={this.state.focused_news} />
                    </Grid>
                 </Grid>
               </div>);
    }
}
export default App;
